import memesData from "./memesData"


export default function Main() {

    let [allMeme, setAllMeme] = React.useState(
        {
            img: memesData,
            topText: "",
            botoomText: ""
        }
    )
    let [randomImg, setRandomImg] = React.useState()
    let button = () => {

        let meme = allMeme.data.memes

        let randomNumber = Math.floor(Math.random(meme) * meme.length)

        let randomMeme = meme[randomNumber].url

        setRandomImg(prevRandomImg => randomMeme)

    }

    return (
        <main>

            <form className="form">
                <input
                    type="text"
                    placeholder="Top text"
                    className="toptext"
                />
                <input
                    type="text"
                    placeholder="Bottom text"
                    className="bottomtext"
                />
            </form>
            <div className="img-container">
                <button onClick={button}
                    className="form--button"
                >
                    Get a new meme image ðŸ–¼
                </button>
                <img className="memeImg" src={randomImg} alt={"#"} ></img>

            </div>

        </main >
    )
}
