import React from "react";

export default function Login() {
    function login() {
        const form = document.querySelector('form');
        form.click = (event) => { event.preventDefault(); }


        const user = {

            mail: 'david@dads',
            password: "1234"
        }
        let mail = document.querySelector('#mail').value
        let password = document.querySelector('#password').value

        let info = {
            mail: mail,
            password: password

        }
        if (info.password === user.password && info.mail === user.mail) {

            window.URL('http://localhost:3000/page1')

        }
        else {
            document.querySelector("h1").innerText = "Try One More Time"
        }
    }


    return (

        <div>
            <form>
                <input id="mail" type="mail" placeholder="david@dads" required></input>
                <input id="password" type="password" placeholder="1234" required></input>
                <button type="submit" onClick={login}>submit</button>
            </form >
            <h1></h1>
        </div >
    )
}
