export default function Main() {
    let [memes, setMemes] = React.useState({
        toptext: "", bootmtext: "", randdomImge: "https://i.imgflip.com/1bij.jpg"
    })
    let [allmemesImg, setAllmemesImg] = React.useState(memesData)

    let button = () => {
        setAllmemesImg(prevAllmemesImg => {

            let meme = allmemesImg.data.memes


            let randomNumber = Math.floor(Math.random(meme) * meme.length)
            let randomMeme = meme[randomNumber]

            setMemes(prevSetMemes => prevSetMemes = randomMeme)


        })


    }

    return (
        <main>

            <form className="form">
                <input
                    type="text"
                    placeholder="Top text"
                    className="toptext"
                />
                <input
                    type="text"
                    placeholder="Bottom text"
                    className="bottomtext"
                />
            </form>
            <div className="img-container">
                <button onClick={button}
                    className="form--button"
                >
                    Get a new meme image ðŸ–¼
                </button>
                <img className="memeImg" src={memes.url} alt={memes.name} ></img>

            </div>

        </main >
    )
}
