import React, { createElement } from "react"
import memesData from "./memesData"


export default function Main() {

    let toptext = document.querySelector(".toptext")
    let bottomtext = document.querySelector(".bottomtext")
    let [memeImg, setMemeImg] = React.useState(1)



    function button() {

        setMemeImg((prevSetMemeImg) => {
            let momse = memesData.data.memes
            const randomIndex = Math.floor(Math.random() * momse.length)
            const randomMeme = momse[randomIndex];
            let img = <img src={randomMeme.url} className="h1"></img>
            prevSetMemeImg + img



        })
    }







    return (
        <main>

            <form className="form">
                <input
                    type="text"
                    placeholder="Top text"
                    className="toptext"
                />
                <input
                    type="text"
                    placeholder="Bottom text"
                    className="bottomtext"
                />
            </form>
            <div className="img-container">
                <button onClick={button}
                    className="form--button"
                >
                    Get a new meme image ðŸ–¼
                </button>

                <h1>{memeImg}</h1>
            </div>

        </main>
    )
}
